package javalearning;

public class Variable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a = 1 ; // Number -> (정확한 표현) integer(정수) : -2-1 0 1 2...
		System.out.println(a);
		
		double b = 1.1; // real number -> double... -2.0 , -1.0, 0, ...
		System.out.println(b);
		
		String c = "Hello World"; //C라는 변수에 문자열이 담김
		System.out.println(c);
		
		//어떤 값을 담을지 명확하게 표현을 해줘야 함.
		//자바와 같은 컴퓨터 언어의 변수는 음료수 용기와 같은 것.
		//이 변수에는 반드시 정수, 문자열이 온다고 정의하면 다른 값이 들어갈 때 아예 동작 안 함.
        //값을 넣을 때 까다로울 뿐  값에 확신을 가질 수 있음.
	}

}
